<?php 	/* Linkleri Listeleme Fonksiyonu */	function links($id='') {		global $db;		if ($id == ''){			echo 'Girdiğiniz id yanlış veya kategori içerisinde link bulunmamaktadır.';		} else {			$sql = "SELECT link, url, icon FROM links WHERE category = ".intval($id)." ORDER BY id";			$sth = $db->prepare($sql);			$db->query("SET NAMES 'utf8'");			$sth->execute();			$result = $sth->fetchAll();			$count = count($result); 			echo '<ul>';			for($i=0;$i<$count;$i++) {				echo '					<li><a href="'.$result[$i]["url"].'" title="'.$result[$i]["link"].'">'.$result[$i]["link"].'</a></li>';			}			echo '</ul>';				}	}?>